import logo from "./logo.svg";
import "./App.css";

import { useState } from "react";

function App() {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [outerarr, setOuterData] = useState([]);
  const [data, setData] = useState([]);

  return (
    <div className="App">
      <form id="form" onClick={(e) => e.preventDefault()}>
        <input
          className="title"
          id="titleField"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <br />
        <input
          className="description"
          id="descriptionField"
          placeholder="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <br />
        <button
          onClick={() => {
            const newData = [...data, title, description];
            setData(newData);
            const mainData = [...outerarr, newData];
            setOuterData(mainData);
            // console.log(mainData);
          }}
        >
          Submit
        </button>
      </form>
      <div className="outerDivForData">
        {data.map((el, i) => {
          return (
            <div className="dataDivOuter">
              {/* <div className="dataDivInner"> */}
                {el} <span className="forEdit">üìã</span>{" "}
                <span className="fordelete">‚ùå</span>
              {/* </div> */}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
